mapper.select:
	select * from (
		select Tb.*, rownum rNum from(
			select * from board

			where ${searchField} like '%' ||#{{searchTxt}'%'

		order by b_idx desc
	) Tb
)where rNum between #{start} and #{end}

mapper.update:
update board
set b_title=#{b_title}, b_content=#{b_content}
where b_idx=#{b_idx} and m_id=#{m_id}
