mapper.select:
select count(*) from club 

	where

		    c_name like #{c_name}


		  and  


		    c_area like #{c_area}


		  and  


		    c_ability like #{c_ability}


		  and  


		    c_gender like #{c_gender}


		  and  


		    c_age like #{c_age}


	order by c_idx desc

