mapper.select:
select *
from club
where c_idx = #{param1}

	     select * from (
	     	select tb.*, rownum rNum from(
	     		select m.*, cm.cm_idx, cm.cm_grade
	   			 from member m inner join clubmember cm 
	   			 on m.m_id = cm.m_id 
	   			 where cm.c_idx = #{param1} order by length(cm_grade) asc
     	 	) tb
	     ) where rNum between #{param2} and #{param3}

select count(*) from club 

	where

		    c_name like #{c_name}


		  and  


		    c_area like #{c_area}


		  and  


		    c_ability like #{c_ability}


		  and  


		    c_gender like #{c_gender}


		  and  


		    c_age like #{c_age}

select * from ( 
	select tb.*, rownum rNum from (
		select * from club order by c_idx desc		
	) tb 
)
where rNum between #{param1} and #{param2}

 	select * from ( 
		 select tb.*, rownum rNum from( 
