					  and  


					    c_age like #{c_age}


			 	order by c_idx desc		 
		 	) tb 
	 	) 
	 where rNum between #{start} and #{end}

select c_idx
from club
where c_name like #{c_name}

select gm.*, m.m_name
from gamemember gm inner join member m on gm.m_id = m.m_id
