mapper.update:
update member set m_pw=#{m_pw}, m_name=#{m_name}, m_birth=#{m_birth}, m_email=#{m_email}, m_phone=#{m_phone}, 
	m_addr=#{m_addr}, m_position=#{m_position}, m_sex=#{m_sex}, m_abil=#{m_abil}, m_foot=#{m_foot}, m_pic=#{m_pic} 
	where m_id=#{m_id}

mapper.select:
select cash 
from member 
where m_id = #{param1}
