mapper.select:
SELECT COUNT(*) FROM stadium

   WHERE s_gu=#{s_gu}

      and s_type=#{s_type}



   WHERE s_name like '%'||#{searchTxt}||'%'


SELECT * FROM (
   SELECT Tb.*, rownum rNum FROM (
      SELECT * FROM stadium

            WHERE s_gu=#{s_gu}

               and s_type=#{s_type}



            WHERE s_name like '%'||#{searchTxt}||'%'

         ORDER BY s_gu DESC
   ) Tb
) 
WHERE rNum BETWEEN #{start} AND #{end}
